definition.script:
pipeline {
    agent any

    parameters {
        cloudifyBlueprintParam(name: 'infra_type', description: 'Type of environment to create', filter: 'infra')
        string(name: 'deployment_id', description: 'Name of environment to create')
    }
    stages {
        stage('Build Cloudify Environment') {
            steps {
                createCloudifyEnv blueprintId: "${params.infra_type}",
                    deploymentId: "${params.deployment_id}",
                    echoOutputs: true,
                    outputFile: 'outputs.json'
            }
        }
        stage('Wait for Input') {
            steps {
                input message: 'Proceed'
            }
        }
        stage('Delete Cloudify Environment') {
            steps {
                deleteCloudifyEnv deploymentId: "${params.deployment_id}"
            }
        }
    }
}
