definition.script:
pipeline {
    agent any

    parameters {
        string(name: 'environment_id', description: 'Environment identifier (translates to Cloudify deployment ID)', trim: true)
        string(name: 'template_url', description: 'URL of the Terraform template archive', trim: true)
        text(name: 'template_vars', description: 'Template variables (as YAML or JSON)')
        password(name: 'aws_access_key_id', description: 'AWS access key ID')
        password(name: 'aws_secret_access_key', description: 'AWS secret access key')
    }

    stages {
        stage('Build Cloudify Environment') {
            steps {
                script {
                    def template_vars_map = readYaml text: "${params.template_vars}"
                    template_vars_map['access_key'] = "${params.aws_access_key_id}"
                    template_vars_map['secret_key'] = "${params.aws_secret_access_key}"
                    cfyTerraform deploymentId: "${params.environment_id}",
                        echoInputs: false,
                        echoEnvData: true,
                        envDataLocation: 'env-data.json',
                        templateUrl: "${params.template_url}",
                        variables: template_vars_map
                }
            }
        }
    }
}
